var translateMap = {
    y: {
        top: -1,
        center: 0,
        bottom: 1,
    },
    x: {
        left: -1,
        center: 0,
        right: 1,
    },
};
export var getTranslateFromOrigin = function (transformOrigin, distance) {
    return {
        x: translateMap.x[transformOrigin.horizontal] * parseInt(distance, 10),
        y: translateMap.y[transformOrigin.vertical] * parseInt(distance, 10),
    };
};
