export function stripUnit(value) {
    return parseInt(("" + value).replace('px', ''), 10);
}
var margin = {
    prop: 'margin',
    direction: ['Top', 'Right', 'Bottom', 'Left'],
};
var marginTop = {
    prop: 'margin',
    direction: ['Top'],
};
var marginRight = {
