    prop: 'padding',
    direction: ['Left', 'Right'],
};
var paddingY = {
    prop: 'padding',
    direction: ['Top', 'Bottom'],
};
var spacingProps = {
    m: margin,
    mt: marginTop,
    mr: marginRight,
    mb: marginBottom,
    ml: marginLeft,
    mx: marginX,
    my: marginY,
    p: padding,
    pt: paddingTop,
    pr: paddingRight,
    pb: paddingBottom,
    pl: paddingLeft,
    px: paddingX,
    py: paddingY,
};
export var space = function (props) {
    var spacingReg = /^[mp][trblxy]?$/;
    var keys = Object.keys(props)
        .filter(function (key) { return spacingReg.test(key); })
        .sort();
    var styles = {};
    keys.forEach(function (key) {
        var value = props[key];
        if (typeof value === 'undefined') {
            return;
        }
        var numericValue = typeof value === 'string' ? stripUnit(value) : value;
        var style = spacingProps[key];
        style.direction.forEach(function (d) {
            var cssAttribute = "" + style.prop + d;
            styles[cssAttribute] = numericValue + "px";
        });
    });
    return styles;
};
export default space;
